  `template_m_lists` varchar(100) NOT NULL DEFAULT '' COMMENT '手机列表模版',
  `template_m_detail` varchar(100) NOT NULL DEFAULT '' COMMENT '手机详情页模版',
  PRIMARY KEY (`id`),
